package week1.day2;

public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=234;
		if(a<0)
		{
			System.out.println("Number is Negative : "+a);
		}
		else if(a>0)
		{
			System.out.println("Number is Positive : "+a);
		}
		else if(a==0)
		{
			System.out.println("Number is Neutral : "+a);
		}
		else
		{
			System.out.println("number is not defined");
		}

	}

}
